import { useEffect, useRef, useState } from "react";
import { Card } from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { PropertyWithDetails, PropertyFilters } from "@shared/schema";
import { Search, MapPin, Navigation, X } from "lucide-react";
import {
  useAddToFavorites,
  useRemoveFromFavorites,
  useIsFavorite,
} from "@/hooks/use-properties";
import { useQueryClient } from "@tanstack/react-query";
import { useTranslation } from "@/lib/i18n";
import { useCurrency } from "@/lib/currency-context";
import { formatPrice } from "@/lib/currency";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { normalizePropertyImageUrl } from "@/lib/utils";

interface PropertyMapProps {
  properties: PropertyWithDetails[];
  filters?: PropertyFilters;
  onFilterChange?: (filters: PropertyFilters) => void;
  onPropertyClick?: (property: PropertyWithDetails) => void;
  onPropertySelect?: (property: PropertyWithDetails) => void;
  onVisiblePropertiesChange?: (count: number) => void;
  userId?: string;
  className?: string;
}

export default function PropertyMap({
  properties,
  filters = {},
  onFilterChange,
  onPropertyClick,
  onPropertySelect,
  onVisiblePropertiesChange,
  userId,
  className,
}: PropertyMapProps) {
  const { t, getLocalized, language } = useTranslation();
  const { preferredCurrency } = useCurrency();
  const { user } = useAuth();

  // Add conditional spacing for Arabic and Kurdish languages
  const isRTL = language === "ar" || language === "kur";
  const spacingFilter = isRTL
    ? "space-x-3 sm:space-x-4"
    : "space-x-2 sm:space-x-3";
  const spacingType = isRTL ? "space-x-3" : "space-x-2";
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);
  const markersRef = useRef<any[]>([]);
  const currentPropertiesRef = useRef<PropertyWithDetails[]>([]);
  const [isLocating, setIsLocating] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const userLocationMarkerRef = useRef<any>(null);
  const [isUserLocationVisible, setIsUserLocationVisible] = useState(false);

  // Track last calculated bounds to avoid unnecessary recalculation
  const lastBoundsRef = useRef<string>('');
  const lastValidCountRef = useRef<number>(0);
  const debounceTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Function to calculate visible properties within map bounds
  const calculateVisibleProperties = (forceUpdate = false) => {
    console.log(`ðŸ” calculateVisibleProperties called with forceUpdate=${forceUpdate}, properties count: ${properties?.length || 0}`);

    if (!mapInstanceRef.current) {
      console.log(`âŒ No map instance available - skipping count update`);
      return; // Don't reset count to 0 during initialization
    }

    // Don't update count if no properties are loaded yet - wait for properties to load
    if (!properties || properties.length === 0) {
      console.log(`âŒ No properties available to calculate - skipping count update`);
      return; // Don't reset count to 0 during loading
    }

    try {
      const bounds = mapInstanceRef.current.getBounds();
      const currentBounds = bounds.toBBoxString();
      const currentZoom = mapInstanceRef.current.getZoom();

      // Create a unique key that includes both bounds and zoom to detect changes
      const boundsAndZoomKey = `${currentBounds}_zoom_${currentZoom}`;

      // Skip calculation if bounds AND zoom haven't changed (optimization)
      // But allow force update for important events like map initialization
      if (!forceUpdate && boundsAndZoomKey === lastBoundsRef.current) {
        console.log(`â­ï¸ Skipping calculation - bounds and zoom unchanged: ${boundsAndZoomKey}`);
        return;
      }
      lastBoundsRef.current = boundsAndZoomKey;

      // Always count individual properties within bounds regardless of zoom level
      // This ensures consistent count when zooming in and out
      const visibleProperties = properties.filter(property => {
        const lat = parseFloat(property.latitude || '');
        const lng = parseFloat(property.longitude || '');

        // Skip properties with invalid coordinates
        if (isNaN(lat) || isNaN(lng) || lat < -90 || lat > 90 || lng < -180 || lng > 180) {
          return false;
        }

        // Check if property coordinates are within current map bounds (Leaflet uses [lat, lng])
        return bounds.contains([lat, lng]);
      });

      console.log(`ðŸ“ Visible properties calculation: ${visibleProperties.length} of ${properties.length} total properties, zoom: ${currentZoom}`);

      // Store the last valid non-zero count
      if (visibleProperties.length > 0) {
        lastValidCountRef.current = visibleProperties.length;
      }

      // If count is 0 and we're during a potential zoom operation, debounce the update
      if (visibleProperties.length === 0 && lastValidCountRef.current > 0) {
        console.log(`â³ Count is 0 but last valid was ${lastValidCountRef.current} - debouncing update`);

        // Clear existing timeout
        if (debounceTimeoutRef.current) {
          clearTimeout(debounceTimeoutRef.current);
        }

        // Set timeout to confirm 0 count after a delay
        debounceTimeoutRef.current = setTimeout(() => {
          console.log(`âœ… Confirming 0 count after debounce delay`);
          onVisiblePropertiesChange?.(0);
        }, 500); // 500ms delay

        return; // Don't update immediately
      }

      // Clear any pending timeout if we have a valid count
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
        debounceTimeoutRef.current = null;
      }

      console.log(`ðŸ“² Calling onVisiblePropertiesChange with count: ${visibleProperties.length}`);
      onVisiblePropertiesChange?.(visibleProperties.length);
    } catch (error) {
      console.log('Error calculating visible properties:', error);
      // On error, show total properties count instead of 0
      onVisiblePropertiesChange?.(properties.length);
    }
  };

  // Favorites hooks
  const addToFavorites = useAddToFavorites();
  const removeFromFavorites = useRemoveFromFavorites();
  const queryClient = useQueryClient();

  // Local state for filters
  const [localFilters, setLocalFilters] = useState<PropertyFilters>(
    filters || {},
  );
  const isLocalUpdate = useRef(false);
  const clickGuard = useRef<number | null>(null);

  // Helper function to get localized property title with fallback
  const getPropertyTitle = (property: any) => {
    return getLocalized(property.title, property.title || "Untitled Property");
  };

  // Pre-calculate currency conversions for all properties  
  const [convertedPrices, setConvertedPrices] = useState<{
    [propertyId: string]: number;
  }>({});

  // Use ref to maintain converted prices across re-renders
  const convertedPricesRef = useRef<{[propertyId: string]: number}>({});

  // Helper function to format price for map popups using pre-calculated conversions
  const formatMapPrice = (property: any) => {
    // Try to get conversion from state first, then from ref as backup
    const convertedAmount = convertedPrices[property.id] ?? convertedPricesRef.current[property.id];
    const hasConversion = convertedAmount !== undefined;

    // Only use converted price if we have the actual conversion result
    if (hasConversion) {
      return formatPrice(
        property.price,
        property.currency,
        property.listingType,
        preferredCurrency,
        convertedAmount,
        t,
      );
    }

    // If no conversion available yet, show original price with original currency
    return formatPrice(
      property.price,
      property.currency,
      property.listingType,
      property.currency, // Use original currency until conversion is available
      parseFloat(property.price),
      t,
    );
  };

  // Check for dark mode and update markers when theme changes
  useEffect(() => {
    const checkDarkMode = () => {
      const newIsDarkMode = document.documentElement.classList.contains("dark");
      setIsDarkMode(newIsDarkMode);

      // Re-render markers when theme changes to update their styling
      if (currentPropertiesRef.current.length > 0) {
        updateMarkersForProperties(currentPropertiesRef.current);
      }
    };

    checkDarkMode();

    // Listen for theme changes
    const observer = new MutationObserver(checkDarkMode);
    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    });

    return () => observer.disconnect();
  }, []);

  // Pre-calculate currency conversions when properties or currency changes
  useEffect(() => {
    const calculateConversions = async () => {
      console.log("ðŸ”„ Currency conversion triggered:", {
        propertiesCount: properties?.length || 0,
        preferredCurrency: preferredCurrency,
        properties: properties?.map((p) => ({
          id: p.id,
          price: p.price,
          currency: p.currency,
        })),
      });

      if (!properties || properties.length === 0) {
        console.log("âŒ No properties to convert");
        return;
      }

      const newConvertedPrices: { [propertyId: string]: number } = {};

      for (const property of properties) {
        if (property.currency === preferredCurrency) {
          // Same currency, no conversion needed
          console.log(
            "âœ… Same currency for",
            property.id,
            ":",
            property.currency,
          );
          newConvertedPrices[property.id] = parseFloat(property.price);
        } else {
          try {
            console.log(
              "ðŸ’± Converting",
              property.id,
              ":",
              property.price,
              property.currency,
              "â†’",
              preferredCurrency,
            );
            // Use the same conversion API as the rest of the app
            const response = await fetch(
              `/api/currency/convert?amount=${property.price}&from=${property.currency}&to=${preferredCurrency}`,
            );
            if (response.ok) {
              const data = await response.json();
              console.log(
                "âœ… Converted",
                property.id,
                ":",
                data.convertedAmount,
              );
              newConvertedPrices[property.id] = data.convertedAmount;
            } else {
              // Fallback to original price if conversion fails
              console.log(
                "âŒ Conversion failed for",
                property.id,
                "- using original price",
              );
              newConvertedPrices[property.id] = parseFloat(property.price);
            }
          } catch (error) {
            // Fallback to original price if conversion fails
            console.log("âŒ Conversion error for", property.id, ":", error);
            newConvertedPrices[property.id] = parseFloat(property.price);
          }
        }
      }

      console.log("ðŸ“Š Final converted prices:", newConvertedPrices);
      setConvertedPrices(newConvertedPrices);
      // Also update the ref to persist across re-renders
      convertedPricesRef.current = { ...newConvertedPrices };
    };

    calculateConversions();
  }, [properties, preferredCurrency]);

  // Function to update popup prices dynamically
  const updatePopupPrices = () => {
    currentPropertiesRef.current.forEach((property) => {
      const priceElement = document.getElementById(
        `popup-price-${property.id}`,
      );
      if (priceElement) {
        const newPrice = formatMapPrice(property);
        priceElement.innerHTML = newPrice;
      }
    });
  };

  // Update markers when currency changes to refresh price displays in popups
  useEffect(() => {
    if (currentPropertiesRef.current.length > 0) {
      updateMarkersForProperties(currentPropertiesRef.current);
      // Also update any currently visible popups
      updatePopupPrices();
    }
  }, [preferredCurrency, convertedPrices]);

  // Recalculate visible properties count when properties change
  useEffect(() => {
    if (mapInstanceRef.current && properties && properties.length > 0) {
      // Use a small delay to ensure the map is fully ready
      const timer = setTimeout(() => {
        calculateVisibleProperties(true); // Force update when properties change
      }, 300);

      return () => clearTimeout(timer);
    }
    // Don't immediately set to 0 when properties.length === 0 
    // because this happens during loading phase
  }, [properties]);

  // Sync local filters with prop changes, but don't overwrite local updates
  useEffect(() => {
    if (isLocalUpdate.current) {
      isLocalUpdate.current = false;
      return;
    }
    setLocalFilters(filters || {});
  }, [filters]);

  // Properties are already filtered from the API, so we use them directly
  // The filtering happens on the server side when onFilterChange is called

  // Add global functions for popup interactions
  useEffect(() => {
    // Define global function for changing slides in map popups
    (window as any).changeSlide = (popupId: string, direction: number) => {
      try {
        const popup = document.getElementById(popupId);
        if (!popup) return;

        const slides = popup.querySelectorAll(".popup-slide");
        const counter = popup.querySelector(".slide-counter");
        if (!slides || slides.length === 0) return;

        // Find current active slide
        let currentIndex = 0;
        slides.forEach((slide, index) => {
          if (slide && (slide as HTMLElement).style.opacity === "1") {
            currentIndex = index;
          }
        });

        // Calculate next index
        let nextIndex = currentIndex + direction;
        if (nextIndex >= slides.length) nextIndex = 0;
        if (nextIndex < 0) nextIndex = slides.length - 1;

        // Hide all slides
        slides.forEach((slide) => {
          if (slide) {
            (slide as HTMLElement).style.opacity = "0";
          }
        });

        // Show next slide
        if (slides[nextIndex]) {
          (slides[nextIndex] as HTMLElement).style.opacity = "1";
        }

        // Update counter
        if (counter) {
          counter.textContent = (nextIndex + 1).toString();
        }
      } catch (error) {
        console.warn("Error in changeSlide function:", error);
      }
    };

