  // Country normalization mapping to handle multilingual country names
  const normalizeCountryName = (country: string): string => {
    const countryNormalizationMap: { [key: string]: string } = {
      // Iraq variations
      "Iraq": "Iraq",
      "Ø§Ù„Ø¹Ø±Ø§Ù‚": "Iraq",
      "Ø¹ÛŽØ±Ø§Ù‚": "Iraq",
      // Add more country mappings as needed
      "Kurdistan": "Kurdistan",
      "ÙƒÙˆØ±Ø¯Ø³ØªØ§Ù†": "Kurdistan",
      "Ú©ÙˆØ±Ø¯Ø³ØªØ§Ù†": "Kurdistan",
      // Turkey variations
      "Turkey": "Turkey",
      "ØªØ±ÙƒÙŠØ§": "Turkey",
      "ØªÙˆØ±Ú©ÛŒØ§": "Turkey",
      // Iran variations
      "Iran": "Iran",
      "Ø¥ÙŠØ±Ø§Ù†": "Iran",
      "Ø¦ÛŽØ±Ø§Ù†": "Iran",
      // Syria variations
      "Syria": "Syria",
      "Ø³ÙˆØ±ÙŠØ§": "Syria",
      "Ø³ÙˆÙˆØ±ÛŒØ§": "Syria",
      // Default cases
      "Unknown Country": "Unknown Country",
    };

    return countryNormalizationMap[country] || country;
  };

  // Function to create zoom-based clusters
  const createZoomBasedClusters = (
    propertiesToCluster: PropertyWithDetails[],
    zoomLevel: number,
  ) => {
    // Three-tier clustering system based on zoom level:
    // Low zoom (< 8): Group by country
    // Medium zoom (8-12): Group by city 
    // High zoom (>= 12): Show individual properties

    if (zoomLevel < 8) {
      // Very zoomed out - group by country
      return createCountryBasedClusters(propertiesToCluster);
    }
    else if (zoomLevel < 12) {
      // Medium zoom - group by city
      return createCityBasedClusters(propertiesToCluster);
    }
    else {
      // Zoomed in - show individual properties (no clustering)
      return propertiesToCluster
        .filter(property => property.latitude && property.longitude)
        .map(property => ({
          properties: [property],
          center: {
            lat: parseFloat(property.latitude || "0"),
            lng: parseFloat(property.longitude || "0"),
          },
          clusterType: "individual",
          count: 1,
        }));
    }
  };

  // Function to create country-based clusters with multilingual support
  const createCountryBasedClusters = (
    propertiesToCluster: PropertyWithDetails[],
  ) => {
    const countryGroups: { [key: string]: PropertyWithDetails[] } = {};

    // Group properties by normalized country name
    propertiesToCluster.forEach((property) => {
      if (!property.latitude || !property.longitude) return;

      // Normalize country name to handle multilingual variations
      const rawCountry = property.country || "Unknown Country";
      const normalizedCountry = normalizeCountryName(rawCountry);

      if (!countryGroups[normalizedCountry]) {
        countryGroups[normalizedCountry] = [];
      }
      countryGroups[normalizedCountry].push(property);
    });

    // Convert country groups to clusters
    return Object.entries(countryGroups).map(([country, properties]) => ({
      properties,
      country, // This will be the normalized country name
      clusterType: "country",
      count: properties.length, // Add count property for cluster marker
      center: {
        lat:
          properties.reduce(
            (sum, p) => sum + parseFloat(p.latitude || "0"),
            0,
          ) / properties.length,
        lng:
          properties.reduce(
            (sum, p) => sum + parseFloat(p.longitude || "0"),
            0,
          ) / properties.length,
      },
    }));
  };

  // Function to create city-based clusters
  const createCityBasedClusters = (
    propertiesToCluster: PropertyWithDetails[],
  ) => {
    const cityGroups: { [key: string]: PropertyWithDetails[] } = {};

    // Group properties by city
    propertiesToCluster.forEach((property) => {
      if (!property.latitude || !property.longitude) return;

      // Use city as grouping key, fallback to 'Unknown City'
      const city = property.city || "Unknown City";

      if (!cityGroups[city]) {
        cityGroups[city] = [];
      }
      cityGroups[city].push(property);
    });

    // Convert city groups to clusters
    return Object.entries(cityGroups).map(([city, properties]) => ({
      properties,
      city,
      clusterType: "city",
      count: properties.length, // Add count property for cluster marker
      center: {
        lat:
          properties.reduce(
            (sum, p) => sum + parseFloat(p.latitude || "0"),
            0,
          ) / properties.length,
        lng:
          properties.reduce(
            (sum, p) => sum + parseFloat(p.longitude || "0"),
            0,
          ) / properties.length,
      },
    }));
  };

  // Function to create distance-based clusters
  const createDistanceBasedClusters = (
    propertiesToCluster: PropertyWithDetails[],
    zoomLevel: number,
  ) => {
    const clusters: any[] = [];
    const processed = new Set<number>();
    // Enhanced cluster distance based on zoom level for better grouping
    const CLUSTER_DISTANCE =
      zoomLevel > 15
        ? 0.003 // Very close clustering for high zoom
        : zoomLevel > 13
          ? 0.005 // Close clustering
          : zoomLevel > 11
            ? 0.01 // Medium clustering
            : zoomLevel > 9
              ? 0.03 // Larger clustering for medium zoom
              : 0.05; // Very large clustering for lower zoom

    propertiesToCluster.forEach((property, index) => {
      if (processed.has(index) || !property.latitude || !property.longitude)
        return;

      const lat = parseFloat(property.latitude);
      const lng = parseFloat(property.longitude);
      const cluster = [property];
      processed.add(index);

      // Find nearby properties
      propertiesToCluster.forEach((otherProperty, otherIndex) => {
        if (
          processed.has(otherIndex) ||
          !otherProperty.latitude ||
          !otherProperty.longitude
        )
          return;

        const otherLat = parseFloat(otherProperty.latitude);
        const otherLng = parseFloat(otherProperty.longitude);

        const distance = Math.sqrt(
          Math.pow(lat - otherLat, 2) + Math.pow(lng - otherLng, 2),
        );

        if (distance <= CLUSTER_DISTANCE) {
          cluster.push(otherProperty);
          processed.add(otherIndex);
        }
      });

      clusters.push({
        properties: cluster,
        center: {
          lat:
            cluster.reduce((sum, p) => sum + parseFloat(p.latitude || "0"), 0) /
            cluster.length,
          lng:
            cluster.reduce(
              (sum, p) => sum + parseFloat(p.longitude || "0"),
              0,
            ) / cluster.length,
        },
      });
    });

    return clusters;
  };

