    // Only update markers if currency conversion is complete or if using same currency
    const conversionNeeded = properties.some(
      (p) => p.currency !== preferredCurrency,
    );
    const conversionReady =
      !conversionNeeded || Object.keys(convertedPrices).length > 0;

    if (conversionReady) {
      updateMarkersForProperties(properties, properties.length);
      // Update visible properties count when properties change
      setTimeout(() => {
        calculateVisibleProperties(true); // Force update when properties change
      }, 200);
    }
  }, [properties, convertedPrices, preferredCurrency]);

  // Update markers when language changes to refresh translations
  useEffect(() => {
    if (currentPropertiesRef.current.length > 0) {
      updateMarkersForProperties(currentPropertiesRef.current, properties.length);
    }
  }, [language]);

  // Update markers function that accepts properties array - always show all markers
  const updateMarkersForProperties = (
    propertiesToShow: PropertyWithDetails[],
    totalPropertiesCount?: number,
  ) => {
    if (
      !mapInstanceRef.current ||
      typeof window === "undefined" ||
      !(window as any).L
    )
      return;

    // Don't update markers if properties array is empty (might be loading)
    if (!propertiesToShow || propertiesToShow.length === 0) {
      return;
    }

    const L = (window as any).L;

    // Clear existing markers safely
    markersRef.current.forEach((marker) => {
      try {
        if (mapInstanceRef.current && marker) {
          mapInstanceRef.current.removeLayer(marker);
        }
      } catch (error) {
        // Ignore cleanup errors
      }
    });
    markersRef.current = [];

    // Get current zoom level to determine clustering strategy
    const currentZoom = mapInstanceRef.current.getZoom();
    const clusters = createZoomBasedClusters(propertiesToShow, currentZoom);
    const isClusteringEnabled = currentZoom < 10;

    clusters.forEach((cluster) => {
      if (cluster.properties.length === 1) {
        // Show individual marker if only one property in cluster
        createSingleMarker(cluster.properties[0], L);
      } else {
        // Show cluster marker if multiple properties are grouped
        // Use the actual count of properties in this specific cluster
        const clusterCount = cluster.count || cluster.properties.length;
        createClusterMarker(cluster, L, isClusteringEnabled, clusterCount);
      }
    });
  };

  const handleFilterChange = (key: string, value: string) => {
    // Prevent duplicate calls within 100ms
    const now = Date.now();
    if (clickGuard.current && now - clickGuard.current < 100) return;
    clickGuard.current = now;

    console.log(`Filter change: ${key} = ${value}`);
    isLocalUpdate.current = true;
    const newFilters = { ...localFilters };

    // Handle special "clear" values
    if (
      value === "any-price" ||
      value === "all-types" ||
      value === "any-bedrooms"
    ) {
      delete newFilters[key as keyof PropertyFilters];
    } else {
      // Check if the same value is already selected (toggle functionality)
      const currentValue = localFilters[key as keyof PropertyFilters];

      if (currentValue === value) {
        // If same value is clicked, unselect it (remove the filter)
        delete newFilters[key as keyof PropertyFilters];
        console.log(`Unselecting filter: ${key}`);
      } else {
        // Convert values to proper types and set new filter
        if (key === "maxPrice" || key === "bedrooms") {
          newFilters[key as keyof PropertyFilters] = parseInt(value) as any;
        } else {
          newFilters[key as keyof PropertyFilters] = value as any;
        }
      }
    }

    // Always maintain the limit for map properties
    newFilters.limit = 100;

    setLocalFilters(newFilters);
    console.log("Updated filters:", newFilters);

    // Immediately trigger parent filter change to call API
    onFilterChange?.(newFilters);
  };

  // Enhanced geolocation function with better error handling and toggle
  const handleGetMyLocation = () => {
    // If user location is already visible, hide it
    if (isUserLocationVisible && userLocationMarkerRef.current) {
      console.log("ðŸ” Hiding user location marker...");
      if (mapInstanceRef.current) {
        mapInstanceRef.current.removeLayer(userLocationMarkerRef.current);
      }
      userLocationMarkerRef.current = null;
      setIsUserLocationVisible(false);
      return;
    }

    console.log("ðŸ” Getting user location...");

    if (!mapInstanceRef.current) {
      console.error("âŒ Map instance not available");
      alert("Map is not ready yet. Please wait a moment and try again.");
      return;
    }

    setIsLocating(true);

    // Check if geolocation is supported
    if (!("geolocation" in navigator)) {
      console.error("âŒ Geolocation not supported by this browser");
      setIsLocating(false);
      alert("Geolocation is not supported by your browser. Please try using a modern browser like Chrome, Firefox, or Safari.");
      return;
    }

    // Check if we're on a secure context (HTTPS or localhost)
    const isSecureContext = window.isSecureContext || location.protocol === 'https:' || location.hostname === 'localhost';
    if (!isSecureContext) {
      console.error("âŒ Geolocation requires secure context (HTTPS)");
      setIsLocating(false);
      alert("Location services require a secure connection (HTTPS). Please make sure you're accessing the site securely.");
      return;
    }

    console.log("âœ… Geolocation is supported and context is secure");

    navigator.geolocation.getCurrentPosition(
      (position) => {
        console.log("âœ… Location obtained successfully:", {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          accuracy: position.coords.accuracy
        });

        const { latitude, longitude } = position.coords;
        const L = (window as any).L;

        if (mapInstanceRef.current && L) {
          // Smoothly fly to user's location with animation
          mapInstanceRef.current.flyTo([latitude, longitude], 15, {
            animate: true,
            duration: 2.5, // 2.5 seconds smooth animation
            easeLinearity: 0.25,
          });

          // Add a more visible pulsing marker for user's location with orange color
          const userLocationIcon = L.divIcon({
            html: `
              <div style="
                position: relative;
                width: 40px;
                height: 40px;
              ">
                <div style="
                  position: absolute;
                  top: 50%;
                  left: 50%;
                  transform: translate(-50%, -50%);
                  width: 40px;
                  height: 40px;
                  background: rgba(255, 120, 0, 0.2);
                  border-radius: 50%;
                  animation: pulse-orange 2s ease-out infinite;
                "></div>
                <div style="
                  position: absolute;
                  top: 50%;
                  left: 50%;
                  transform: translate(-50%, -50%);
                  background: #FF7800;
                  width: 16px;
                  height: 16px;
                  border-radius: 50%;
                  border: 3px solid white;
                  box-shadow: 0 2px 10px rgba(255, 120, 0, 0.6);
                  z-index: 10;
                ">
                  <div style="
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    width: 6px;
                    height: 6px;
                    background: white;
                    border-radius: 50%;
                  "></div>
                </div>
              </div>
              <style>
                @keyframes pulse-orange {
                  0% {
                    transform: translate(-50%, -50%) scale(0.5);
                    opacity: 1;
                  }
                  100% {
                    transform: translate(-50%, -50%) scale(2.5);
                    opacity: 0;
                  }
                }
              </style>
            `,
            className: "user-location-marker",
            iconSize: [40, 40],
            iconAnchor: [20, 20],
          });

          // Remove any existing user location marker
          if (userLocationMarkerRef.current) {
            mapInstanceRef.current.removeLayer(userLocationMarkerRef.current);
          }

          // Create and add the user location marker (without popup)
          userLocationMarkerRef.current = L.marker([latitude, longitude], {
            icon: userLocationIcon,
          }).addTo(mapInstanceRef.current);

          // Set visibility state
          setIsUserLocationVisible(true);

          console.log("âœ… User location marker added to map at", latitude, longitude);
        } else {
          console.error("âŒ Map instance or Leaflet not available");
        }

        // Send location data to backend for tracking
        const sendLocationToBackend = async () => {
          try {
            console.log("ðŸ“¤ Sending location data to backend...");

            const locationData = {
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
              accuracy: Math.round(position.coords.accuracy), // Round to integer
              userId: user?.id || null,
              metadata: {
                userAgent: navigator.userAgent,
                language: navigator.language,
                permissionStatus: 'granted',
                city: undefined, // Will be determined by server if available
                country: undefined // Will be determined by server if available
              }
            };

            const response = await apiRequest('POST', '/api/client-locations', locationData);

            if (response.ok) {
              console.log("âœ… Location data sent successfully to backend");

              // Invalidate admin dashboard cache so it updates in real-time
              queryClient.invalidateQueries({ queryKey: ['/api/admin/client-locations'] });
            } else {
              console.error("âŒ Failed to send location data to backend");
            }
          } catch (error) {
            console.error("âŒ Error sending location data to backend:", error);
            // Don't show user error for tracking - this is background functionality
          }
        };

        // Send location data to backend (non-blocking)
        sendLocationToBackend();

        setIsLocating(false);
      },
      (error) => {
        console.error("âŒ Geolocation error:", error);
        console.error("âŒ Error details:", {
          code: error?.code,
          message: error?.message,
          PERMISSION_DENIED: error?.PERMISSION_DENIED,
          POSITION_UNAVAILABLE: error?.POSITION_UNAVAILABLE,
          TIMEOUT: error?.TIMEOUT
        });
        setIsLocating(false);

        let errorMessage = "Unable to get your location. ";

        // Handle both standard error codes and empty error objects
        if (!error || !error.code) {
          errorMessage += "Location service is not responding. Please check:\n\n";
          errorMessage += "â€¢ Your device has location services enabled\n";
          errorMessage += "â€¢ Your browser has permission to access location\n";
          errorMessage += "â€¢ You're using HTTPS or localhost\n";
          errorMessage += "\nTry refreshing the page and allowing location access when prompted.";
        } else {
          switch (error.code) {
            case error.PERMISSION_DENIED:
              errorMessage += "You denied location access. To enable location services:\n\n";
              errorMessage += "â€¢ Click the location icon in your browser's address bar\n";
              errorMessage += "â€¢ Select 'Allow' for location access\n";
              errorMessage += "â€¢ Refresh the page and try again";
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage += "Location information is unavailable. Please check:\n\n";
              errorMessage += "â€¢ Your device's location services are enabled\n";
              errorMessage += "â€¢ You have a stable internet connection\n";
              errorMessage += "â€¢ Try moving to a location with better GPS signal";
              break;
            case error.TIMEOUT:
              errorMessage += "Location request timed out. Please:\n\n";
              errorMessage += "â€¢ Check your internet connection\n";
              errorMessage += "â€¢ Try again in a moment\n";
              errorMessage += "â€¢ Make sure location services are enabled on your device";
              break;
            default:
              errorMessage += "An unknown error occurred. Please try again or check your browser settings.";
              break;
          }
        }

        alert(errorMessage);
      },
      {
        enableHighAccuracy: false, // Set to false for faster response
        timeout: 15000, // Increased timeout to 15 seconds
        maximumAge: 60000, // 1 minute cache
      },
    );
  };

  return (
    <div className={className}>
      <div className="relative">
        {/* Map Container - Full Size */}
        <div className="relative h-screen" data-testid="property-map">
          <div ref={mapRef} className="w-full h-full" />

          {/* Legend Overlay on Map */}
          <div className="fixed bottom-4 left-4 right-4 md:bottom-6 md:left-6 md:right-6 z-[1000] transition-all duration-500 ease-out">
            <div className="p-4 md:p-5 transition-all duration-300 space-y-4">
              {/* Get My Location / Close Location Button - Positioned above filters */}
              <div className="flex justify-end">
                <Button
                  onClick={handleGetMyLocation}
                  disabled={isLocating}
                  className={`relative w-10 h-10 rounded-full backdrop-blur-md border shadow-lg transition-all duration-300 hover:scale-105 hover:shadow-xl ${isUserLocationVisible ? 'bg-red-500 hover:bg-red-600 border-red-400' : 'bg-orange-500 hover:bg-orange-600 border-orange-400'} text-white flex-shrink-0 flex items-center justify-center gap-2 overflow-hidden ${isLocating ? "animate-pulse" : ""}`}
                  data-testid="footer-location-button"
                >
                  {!isLocating && !isUserLocationVisible && (
                    <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent transform -skew-x-12 animate-sweep" />
                  )}
                  {isUserLocationVisible ? (
                    <X className="relative h-4 w-4 text-white z-10" />
                  ) : (
                    <Navigation
                      className={`relative h-4 w-4 text-white z-10 transition-transform duration-300 ${isLocating ? "animate-slow-spin" : ""}`}
                    />
                  )}
                </Button>
              </div>

              {/* Filter buttons row */}
              <div className="flex flex-wrap items-center justify-center gap-3 sm:gap-4 md:gap-6 text-sm">
                <div
                  className={`flex items-center ${spacingFilter} p-2 rounded-xl backdrop-blur-md border shadow-lg transition-all duration-300 hover:scale-105 hover:shadow-xl cursor-pointer ${
                    localFilters.listingType === "sale"
                      ? "bg-red-100 dark:bg-red-900/40 border-red-300 dark:border-red-600"
                      : "bg-white/95 dark:bg-gray-900/95 border-gray-200/50 dark:border-gray-600/50 hover:bg-white dark:hover:bg-gray-800 hover:border-gray-300 dark:hover:border-gray-500"
                  }`}
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    handleFilterChange("listingType", "sale");
                  }}
                >
                  <div
                    className={`w-3 h-3 sm:w-4 sm:h-4 bg-red-500 rounded-full flex-shrink-0 shadow-lg ${localFilters.listingType === "sale" ? "animate-pulse" : ""}`}
                  ></div>
                  <span
                    className={`font-semibold text-sm drop-shadow-lg ${localFilters.listingType === "sale" ? "text-red-700 dark:text-red-300" : "text-black dark:text-white"}`}
                  >
                    {t("filter.forSale")}
                  </span>
                </div>
