import { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Upload, X, Image as ImageIcon, AlertCircle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface ImageUploadProps {
  value: string[];
  onChange: (images: string[]) => void;
  maxFiles?: number;
  maxSize?: number; // in MB
  className?: string;
}

export default function ImageUpload({ 
  value = [], 
  onChange, 
  maxFiles = 10, 
  maxSize = 5,
  className = '' 
}: ImageUploadProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const { toast } = useToast();

  const uploadToServer = async (file: File): Promise<string> => {
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch('/api/upload/properties', {
      method: 'POST',
      body: formData,
      credentials: 'include'
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.message || 'Upload failed');
    }

    const result = await response.json();
    return result.url; // Returns the server path like /uploads/properties/filename.jpg
  };

  const validateFile = (file: File): boolean => {
    if (!file.type.startsWith('image/')) {
      toast({
        title: 'Invalid file type',
        description: 'Please upload only image files (JPG, PNG, GIF, etc.)',
        variant: 'destructive',
      });
      return false;
    }

    if (file.size > maxSize * 1024 * 1024) {
      toast({
        title: 'File too large',
        description: `Please upload images smaller than ${maxSize}MB`,
        variant: 'destructive',
      });
      return false;
    }

    return true;
  };

  const handleFileUpload = useCallback(async (files: FileList) => {
    if (value.length + files.length > maxFiles) {
      toast({
        title: 'Too many files',
        description: `You can upload maximum ${maxFiles} images`,
        variant: 'destructive',
      });
      return;
    }

    setUploading(true);
    const newImages: string[] = [];

    try {
      for (let i = 0; i < files.length; i++) {
        const file = files[i];
        if (validateFile(file)) {
          try {
            const serverUrl = await uploadToServer(file);
            newImages.push(serverUrl);
          } catch (uploadError) {
            console.error('Failed to upload file:', file.name, uploadError);
            toast({
              title: 'Upload failed',
              description: `Failed to upload ${file.name}. Please try again.`,
              variant: 'destructive',
            });
          }
        }
      }

      if (newImages.length > 0) {
        onChange([...value, ...newImages]);
        toast({
          title: 'Images uploaded',
          description: `Successfully uploaded ${newImages.length} image(s) to server`,
        });
      }
    } catch (error) {
      console.error('Upload error:', error);
      toast({
        title: 'Upload failed',
        description: 'Failed to process some images. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setUploading(false);
    }
  }, [value, onChange, maxFiles, maxSize, toast]);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files);
    }
  }, [handleFileUpload]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  }, []);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      handleFileUpload(files);
    }
    e.target.value = ''; // Reset input
  }, [handleFileUpload]);

  const removeImage = useCallback((index: number) => {
    const newImages = [...value];
    newImages.splice(index, 1);
    onChange(newImages);
    toast({
      title: 'Image removed',
      description: 'Image has been removed from the list',
    });
  }, [value, onChange, toast]);

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Upload Area */}
      <Card 
        className={`relative border-2 border-dashed transition-colors ${
          isDragging 
            ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20' 
            : 'border-gray-300 dark:border-gray-600 hover:border-gray-400 dark:hover:border-gray-500'
        }`}
      >
        <CardContent 
          className="p-8 text-center cursor-pointer"
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onClick={() => document.getElementById('file-input')?.click()}
        >
          <input
            id="file-input"
            type="file"
            multiple
            accept="image/*"
            className="hidden"
            onChange={handleFileInput}
            disabled={uploading || value.length >= maxFiles}
          />

          <div className="space-y-4">
            <div className="flex justify-center">
              {uploading ? (
                <div className="animate-spin h-8 w-8 border-4 border-blue-600 border-t-transparent rounded-full" />
              ) : (
                <Upload className={`h-8 w-8 ${isDragging ? 'text-blue-500' : 'text-gray-400'}`} />
              )}
            </div>

            <div>
              <p className="text-lg font-medium">
                {uploading ? 'Uploading images...' : 'Upload Property Images'}
              </p>
              <p className="text-sm text-muted-foreground mt-1">
                Drag & drop images here, or click to select files
              </p>
              <p className="text-xs text-muted-foreground mt-2">
                Supports JPG, PNG, WebP up to {maxSize}MB each. Maximum {maxFiles} images.
              </p>
            </div>

            {value.length > 0 && (
              <Badge variant="secondary" className="text-xs">
                {value.length}/{maxFiles} images uploaded
              </Badge>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Image Previews */}
      {value.length > 0 && (
        <div>
          <h4 className="text-sm font-medium mb-3 flex items-center gap-2">
            <ImageIcon className="h-4 w-4" />
            Uploaded Images ({value.length})
          </h4>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {value.map((image, index) => (
              <Card key={index} className="relative group overflow-hidden">
                <CardContent className="p-2">
                  <div className="relative aspect-square">
                    <img
                      src={image}
                      alt={`Property image ${index + 1}`}
                      className="w-full h-full object-cover rounded-md"
                      loading="lazy"
                      decoding="async"
                    />
                    <Button
                      variant="destructive"
                      size="icon"
                      className="absolute top-1 right-1 h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeImage(index);
                      }}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                    <Badge 
                      variant="secondary" 
                      className="absolute bottom-1 left-1 text-xs px-1 py-0"
                    >
                      #{index + 1}
                    </Badge>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Upload Limits Info */}
      {value.length === 0 && (
        <div className="flex items-center gap-2 text-xs text-muted-foreground bg-muted/50 p-3 rounded-lg">
          <AlertCircle className="h-4 w-4" />
          <span>
            Add multiple high-quality images to showcase your property. The first image will be used as the main photo.
          </span>
        </div>
      )}
    </div>
  );
}

client/src/pages/favorites.tsx.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import PropertyCard from "@/components/property-card";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { SEOHead } from "@/components/SEOHead";
import { useFavorites, useProperties } from "@/hooks/use-properties";
import { useLocalFavorites } from "@/hooks/use-local-favorites";
import { useAuth } from "@/hooks/useAuth";
import { useTranslation } from "@/lib/i18n";
import { ArrowLeft, Heart, Home as HomeIcon } from "lucide-react";
import type { Property } from "@/types";

export default function FavoritesPage() {
  const { user } = useAuth();
  const userId = user?.id;
  const { data: serverFavorites, isLoading: serverLoading, error: serverError } = useFavorites(userId);
  const { data: allProperties, isLoading: propertiesLoading } = useProperties();
  const localFavorites = useLocalFavorites();
  const [, setLocation] = useLocation();
  const { t } = useTranslation();

  // Determine which favorites to show
  const favorites = userId 
    ? serverFavorites 
    : allProperties?.filter(property => localFavorites.isFavorite(property.id)) || [];

  const isLoading = userId ? serverLoading : propertiesLoading;
  const error = userId ? serverError : null;

  const handleMapClick = (property: Property) => {
    // Navigate to home page with property ID to show on map
    setLocation(`/?showProperty=${property.id}`);
  };

  // Generate favorites page structured data
  const getFavoritesStructuredData = () => {
    const totalFavorites = favorites?.length || 0;

    return {
      "@context": "https://schema.org",
      "@type": "CollectionPage",
      "name": t('favorites.structuredDataName'),
      "description": `View your ${totalFavorites} ${t('favorites.structuredDataDescription')}`,
      "numberOfItems": totalFavorites,
      "isPartOf": {
        "@type": "WebSite",
        "name": "MapEstate",
        "url": window.location.origin
      }
    };
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-orange-100 dark:from-gray-900 dark:to-gray-800">
        <div className="container mx-auto px-4 py-8">
          <div className="flex justify-center items-center min-h-[400px]">
            <div className="text-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
              <p className="text-muted-foreground">{t('favorites.loadingFavorites')}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-orange-100 dark:from-gray-900 dark:to-gray-800">
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">
            <p className="text-red-600 dark:text-red-400">{t('favorites.errorLoading')}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      <SEOHead
        pageType="favorites"
        ogImage={`/uploads/properties/1759082074149-xrejrtvx6.jpg`}
        structuredData={getFavoritesStructuredData()}
        breadcrumbs={[
          { name: 'Home', url: '/' },
          { name: 'Favorites', url: '/favorites' }
        ]}
      />
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center gap-4">
            <Link href="/">
              <Button variant="ghost" size="sm" className="flex items-center gap-2" data-testid="back-to-home">
                <ArrowLeft className="h-4 w-4" />
                {t('property.backToHome')}
              </Button>
            </Link>
          </div>
        </div>

        {/* Favorites List */}
        {!favorites || favorites.length === 0 ? (
          <Card className="bg-white/20 dark:bg-black/20 backdrop-blur-xl border-white/30 dark:border-white/10">
            <CardContent className="py-16 text-center">
              <div className="text-center" style={{ textAlign: 'center' }}>
                <Heart className="h-16 w-16 text-gray-300 dark:text-gray-600 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-foreground mb-2" style={{ textAlign: 'center' }}>{t('favorites.noFavoritesYet')}</h3>
                <p className="text-muted-foreground mb-6" style={{ textAlign: 'center' }}>
                  {t('favorites.noFavoritesDescription')}
                </p>
              </div>
            </CardContent>
          </Card>
        ) : (
          <>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 2xl:grid-cols-4 gap-4 sm:gap-6 lg:gap-8">
              {favorites.map((property) => (
                <PropertyCard 
                  key={property.id} 
                  property={property} 
                  userId={userId} 
                  showMapButton={true}
                  onMapClick={handleMapClick}
                />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

