import type { Express, Response } from "express";
import express from "express";
import { createServer, type Server } from "http";
import multer from "multer";
import path from "path";
import fs from "fs";
import { fileURLToPath } from 'url';
import { StorageFactory } from "./storageFactory";

const __dirname = path.dirname(fileURLToPath(import.meta.url));
import { 
  insertPropertySchema, updatePropertySchema, insertInquirySchema, insertFavoriteSchema, insertUserSchema,
  insertWaveSchema, insertCustomerWavePermissionSchema, insertCurrencyRateSchema, updateCurrencyRateSchema,
  insertClientLocationSchema
} from "@shared/schema";
import { extractPropertyIdentifier } from "@shared/slug-utils";
import { hashPassword, requireAuth, requireRole, requireAnyRole, populateUser, validateLanguagePermission, checkExpiration } from "./auth";
import session from "express-session";
import { z } from "zod";
import sitemapRouter from "./routes/sitemap";
import { registerPerformanceRoutes } from "./routes/performance";
import { 
  authRateLimit, searchRateLimit, apiRateLimit, adminRateLimit, 
  heavyOperationRateLimit, uploadRateLimit 
} from "./middleware/rateLimiting";
import { 
  cacheControl, handleConditionalRequest, trackQueryPerformance,
  performanceMonitor 
} from "./middleware/performance";

// SSE client management
const sseClients = new Set<Response>();

function broadcastToSSEClients(event: string, data: any) {
  // Structure payload to avoid property type field overwriting event type
  const payload = { 
    eventType: event,
    data: data
  };

  // Send both custom event and default message for maximum compatibility
  const customEventMessage = `event: ${event}\ndata: ${JSON.stringify(data)}\n\n`;
  const defaultMessage = `data: ${JSON.stringify(payload)}\n\n`;

  console.log(`ðŸ“¡ Broadcasting ${event} to ${sseClients.size} connected clients:`, data.title || data.id);

  // Send to all connected clients
  sseClients.forEach(client => {
    try {
      // Send both formats to ensure compatibility
      client.write(customEventMessage);
      client.write(defaultMessage);
    } catch (error) {
      console.error(`âŒ Failed to send ${event} to client:`, error);
      // Remove disconnected clients
      sseClients.delete(client);
    }
  });
}

import type { IStorage } from "./storage";

export async function registerRoutes(app: Express, storageInstance?: IStorage): Promise<Server> {
  // Use provided storage or get from factory
  const storage = storageInstance || StorageFactory.getInstance();

  // Session configuration
  app.use(session({
    secret: process.env.SESSION_SECRET || 'your-secret-key-change-in-production',
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: false, // Set to true in production with HTTPS
      httpOnly: true,
      maxAge: 24 * 60 * 60 * 1000 // 24 hours
    }
  }));

  // Add user to all requests
  app.use(populateUser);

  // SEO routes
  app.use(sitemapRouter);

  // Performance monitoring routes
  registerPerformanceRoutes(app);

  // Authentication routes with rate limiting
  app.post("/api/auth/login", authRateLimit, async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Username and password are required" });
      }

      const user = await storage.authenticateUser(username, password);

      if (!user) {
        return res.status(401).json({ message: "Invalid username or password" });
      }

      // Set session
      req.session.userId = user.id;

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ user: userWithoutPassword, message: "Login successful" });
    } catch (error) {
      console.error("Login error:", error);
      res.status(500).json({ message: "Login failed" });
    }
  });

  app.post("/api/auth/logout", authRateLimit, (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Logout failed" });
      }
      res.json({ message: "Logout successful" });
    });
  });

  app.get("/api/auth/me", requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Fetch user's language permissions
      const userLanguages = await storage.getUserLanguages(req.session.userId!);
      const allowedLanguages = userLanguages.map(ul => ul.language);

      const { password: _, ...userWithoutPassword } = user;

      // Include language permissions in the response
      const userWithLanguages = {
        ...userWithoutPassword,
        allowedLanguages: allowedLanguages.length > 0 ? allowedLanguages : ['en'] // Default to English if no languages assigned
      };

      res.json(userWithLanguages);
    } catch (error) {
      res.status(500).json({ message: "Failed to get user" });
    }
  });

  // Get user's language permissions
  app.get("/api/users/:id/languages", requireRole("admin"), async (req, res) => {
    try {
      const { id } = req.params;
      const userLanguages = await storage.getUserLanguages(id);
      res.json(userLanguages.map(ul => ul.language));
    } catch (error) {
      res.status(500).json({ message: "Failed to get user languages" });
    }
  });

  // Add language permission to user (super admin only)
  app.post("/api/users/:id/languages", requireRole("super_admin"), async (req, res) => {
    try {
      const { id } = req.params;
      const { language } = req.body;

      if (!language) {
        return res.status(400).json({ message: "Language is required" });
      }

      // Check if language is supported
      const supportedLanguages = ["en", "ar", "kur"];
      if (!supportedLanguages.includes(language)) {
        return res.status(400).json({ 
          message: `Unsupported language '${language}'. Supported languages: ${supportedLanguages.join(', ')}` 
        });
      }

      // Check if user exists
      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      await storage.addUserLanguage(id, language);
      res.json({ message: `Language '${language}' added to user successfully` });
    } catch (error) {
      console.error("Failed to add user language:", error);
      res.status(500).json({ message: "Failed to add user language" });
    }
  });

  // Remove language permission from user (super admin only)
  app.delete("/api/users/:id/languages/:language", requireRole("super_admin"), async (req, res) => {
    try {
      const { id, language } = req.params;

      // Check if user exists
      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      await storage.removeUserLanguage(id, language);
      res.json({ message: `Language '${language}' removed from user successfully` });
    } catch (error) {
      console.error("Failed to remove user language:", error);
      res.status(500).json({ message: "Failed to remove user language" });
    }
  });

  // Set all language permissions for user (super admin only)
  app.put("/api/users/:id/languages", requireRole("super_admin"), async (req, res) => {
    try {
      const { id } = req.params;
      const { languages } = req.body;

      if (!Array.isArray(languages)) {
        return res.status(400).json({ message: "Languages must be an array" });
      }

      // Check if all languages are supported
      const supportedLanguages = ["en", "ar", "kur"];
      const invalidLanguages = languages.filter(lang => !supportedLanguages.includes(lang));
      if (invalidLanguages.length > 0) {
        return res.status(400).json({ 
          message: `Unsupported languages: ${invalidLanguages.join(', ')}. Supported languages: ${supportedLanguages.join(', ')}` 
        });
      }

      // Check if user exists
      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Remove all existing languages
      const currentLanguages = await storage.getUserLanguages(id);
      for (const userLang of currentLanguages) {
        await storage.removeUserLanguage(id, userLang.language);
      }

      // Add new languages
      for (const language of languages) {
        await storage.addUserLanguage(id, language);
      }

      res.json({ message: `User language permissions updated successfully. Languages: ${languages.join(', ')}` });
    } catch (error) {
      console.error("Failed to update user languages:", error);
      res.status(500).json({ message: "Failed to update user languages" });
    }
  });

  // Fix existing users language permissions (super admin only)
  app.post("/api/users/fix-language-permissions", requireRole("super_admin"), async (req, res) => {
    try {
      await storage.fixExistingUsersLanguagePermissions();
      res.json({ message: "Successfully fixed language permissions for all existing users" });
    } catch (error) {
      console.error("Failed to fix user language permissions:", error);
      res.status(500).json({ message: "Failed to fix user language permissions" });
