      if (!['avatar', 'customer', 'properties'].includes(type)) {
        return res.status(400).json({ message: "Invalid upload type. Use 'avatar', 'customer', or 'properties'" });
      }

      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

