  app.post("/api/admin/users", adminRateLimit, requireRole("admin"), async (req, res) => {
    try {
      console.log('ðŸ” DEBUG: Raw request body:', JSON.stringify(req.body, null, 2));
      console.log('ðŸ” DEBUG: User role:', req.user?.role);

      // Preprocess the request body to handle date conversion
      const processedBody = { ...req.body };

      // Convert expiresAt string to Date if provided and not empty
      if (processedBody.expiresAt) {
        if (typeof processedBody.expiresAt === 'string' && processedBody.expiresAt.trim() !== '') {
          const dateObj = new Date(processedBody.expiresAt);
          if (!isNaN(dateObj.getTime())) {
            processedBody.expiresAt = dateObj;
          } else {
            delete processedBody.expiresAt;
          }
        } else {
          delete processedBody.expiresAt;
        }
      }

      // Only admins and super admins can set language permissions for other users
      if (processedBody.allowedLanguages && req.user?.role !== 'admin' && req.user?.role !== 'super_admin') {
        return res.status(403).json({ 
          message: "Only admins can set language permissions" 
        });
      }

      // Extract allowedLanguages before validation since it's not in the main schema
      const allowedLanguages = processedBody.allowedLanguages;
      delete processedBody.allowedLanguages;

      console.log('ðŸ” DEBUG: Processed body before validation (avatar truncated):', {
        ...processedBody,
        avatar: processedBody.avatar ? `[Base64 image ${processedBody.avatar.length} chars]` : undefined
      });

      const validatedData = insertUserSchema.parse(processedBody);

      // Hash password
      const hashedPassword = await hashPassword(validatedData.password);

      const user = await storage.createUser({
        ...validatedData,
        password: hashedPassword
      });

      // Handle language permissions after user creation (only for admin/super admin)
      if (allowedLanguages && (req.user?.role === 'admin' || req.user?.role === 'super_admin')) {
        // Remove any existing languages first
        const existingLanguages = await storage.getUserLanguages(user.id);
        for (const lang of existingLanguages) {
          await storage.removeUserLanguage(user.id, lang.language);
        }

        // Add the new languages
        for (const language of allowedLanguages) {
          await storage.addUserLanguage(user.id, language);
        }
      }

      const { password: _, ...userWithoutPassword } = user;
      res.status(201).json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid user data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create user" });
    }
  });

