  // SSE stream for real-time property updates
  app.get("/api/properties/stream", (req, res) => {
    console.log('ðŸ”Œ New SSE connection established');

    // Set SSE headers with anti-buffering measures
    res.writeHead(200, {
      'Content-Type': 'text/event-stream',
      'Cache-Control': 'no-cache, no-transform',
      'Connection': 'keep-alive',
      'Keep-Alive': 'timeout=60, max=1000',
      'Content-Encoding': 'identity',
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Headers': 'Cache-Control',
      'X-Accel-Buffering': 'no' // Prevent buffering by Nginx
    });

    // Flush headers to prevent intermediary buffering
    res.flushHeaders();

    // Send 2KB padding to force streaming start (prevents proxy buffering)
    res.write(':' + ' '.repeat(2048) + '\n');
    res.write('retry: 10000\n\n');
    if (res.flush) res.flush();
    console.log('ðŸ“¡ SSE padding sent, forcing stream start');

    // Send initial connection event
    res.write(`data: ${JSON.stringify({ type: 'connected', message: 'SSE connection established' })}\n\n`);
    if (res.flush) res.flush();
    console.log('ðŸ“¡ SSE initial connection message sent and flushed');

    // Add client to SSE clients set
    sseClients.add(res);
    console.log(`ðŸ“Š SSE clients connected: ${sseClients.size}`);

    // Set up heartbeat to keep connection alive
    const heartbeat = setInterval(() => {
      try {
        const heartbeatData = `data: ${JSON.stringify({ type: 'heartbeat', timestamp: new Date().toISOString() })}\n\n`;
        res.write(heartbeatData);
        if (res.flush) res.flush();
        console.log('ðŸ’“ SSE heartbeat sent and flushed');
      } catch (error) {
        console.log('ðŸ’” SSE heartbeat failed, removing client:', (error as Error).message);
        clearInterval(heartbeat);
        sseClients.delete(res);
        console.log(`ðŸ“Š SSE clients connected: ${sseClients.size}`);
      }
    }, 15000); // Send heartbeat every 15 seconds

    // Handle client disconnect
    req.on('close', () => {
      console.log('ðŸ”Œ SSE client disconnected (close)');
      clearInterval(heartbeat);
      sseClients.delete(res);
      console.log(`ðŸ“Š SSE clients connected: ${sseClients.size}`);
    });

    req.on('error', (error) => {
      console.log('ðŸ”Œ SSE client disconnected (error):', error.message);
      clearInterval(heartbeat);
      sseClients.delete(res);
      console.log(`ðŸ“Š SSE clients connected: ${sseClients.size}`);
    });
  });

