import { db, initializeDb } from "./db";
import { propertyImages, properties, users } from "@shared/schema";
import { eq } from "drizzle-orm";
import fs from "fs";
import path from "path";
import { fileURLToPath } from 'url';

const __dirname = path.dirname(fileURLToPath(import.meta.url));

interface ImageInfo {
  id: number;
  propertyId: string;
  propertyTitle: string;
  imageUrl: string;
  sortOrder: number;
  altText: string | null;
  fileExists: boolean;
  filePath: string;
  fileSize?: string;
}

async function showAllImages() {
  try {
    console.log("ðŸ”§ Initializing MySQL VPS connection...");
    await initializeDb();

    console.log("ðŸ“Š Fetching all images from database...\n");

    // Get all property images with property details
    const images = await db()
      .select({
        id: propertyImages.id,
        propertyId: propertyImages.propertyId,
        imageUrl: propertyImages.imageUrl,
        sortOrder: propertyImages.sortOrder,
        altText: propertyImages.altText,
        propertyTitle: properties.title,
      })
      .from(propertyImages)
      .leftJoin(properties, eq(propertyImages.propertyId, properties.id))
      .orderBy(propertyImages.propertyId, propertyImages.sortOrder);

    if (images.length === 0) {
      console.log("âš ï¸ No images found in database");
      return;
    }

    console.log(`âœ… Found ${images.length} images in database\n`);
    console.log("="  .repeat(100));

    // Determine the uploads directory path
    const uploadsPath = path.join(__dirname, "../server/uploads");

    const imageDetails: ImageInfo[] = [];

    // Check each image file
    for (const img of images) {
      let filePath = "";
      let fileExists = false;
      let fileSize = "";

      // Parse image URL to get file path
      const imageUrl = img.imageUrl || "";

      if (imageUrl.startsWith("http://") || imageUrl.startsWith("https://")) {
        // External URL
        filePath = imageUrl;
        fileExists = true; // Assume external URLs exist
      } else {
        // Local file - check if it exists
        const localPath = imageUrl.startsWith("/") ? imageUrl.substring(1) : imageUrl;
        filePath = path.join(__dirname, "..", localPath);

        try {
          const stats = fs.statSync(filePath);
          fileExists = true;
          const sizeInKB = (stats.size / 1024).toFixed(2);
          fileSize = `${sizeInKB} KB`;
        } catch (error) {
          fileExists = false;
        }
      }

      imageDetails.push({
        id: img.id,
        propertyId: img.propertyId,
        propertyTitle: img.propertyTitle || "Unknown Property",
        imageUrl: img.imageUrl,
        sortOrder: img.sortOrder || 0,
        altText: img.altText,
        fileExists,
        filePath,
        fileSize,
      });
    }

    // Group images by property
    const imagesByProperty = imageDetails.reduce((acc, img) => {
      if (!acc[img.propertyId]) {
        acc[img.propertyId] = [];
      }
      acc[img.propertyId].push(img);
      return acc;
    }, {} as Record<string, ImageInfo[]>);

    // Display images grouped by property
    let propertyCount = 0;
    let existingCount = 0;
    let missingCount = 0;

    for (const [propertyId, propertyImages] of Object.entries(imagesByProperty)) {
      propertyCount++;
      const firstImage = propertyImages[0];

      console.log(`\nðŸ“¦ Property ${propertyCount}: ${firstImage.propertyTitle}`);
      console.log(`   Property ID: ${propertyId}`);
      console.log(`   Total Images: ${propertyImages.length}`);
      console.log(`   ${"-".repeat(90)}`);

      propertyImages.forEach((img, index) => {
        const status = img.fileExists ? "âœ…" : "âŒ";
        const sizeInfo = img.fileSize ? ` (${img.fileSize})` : "";

        if (img.fileExists) {
          existingCount++;
        } else {
          missingCount++;
        }

        console.log(`   ${status} Image ${index + 1}:`);
        console.log(`      ID: ${img.id}`);
        console.log(`      URL: ${img.imageUrl}`);
        console.log(`      Sort Order: ${img.sortOrder}`);
        console.log(`      Alt Text: ${img.altText || "N/A"}`);
        console.log(`      File Path: ${img.filePath}${sizeInfo}`);
        console.log(`      Status: ${img.fileExists ? "File Exists" : "File Missing"}`);
        console.log("");
      });
    }

    // Summary
    console.log("=" .repeat(100));
    console.log("\nðŸ“ˆ Summary:");
    console.log(`   Total Properties with Images: ${propertyCount}`);
    console.log(`   Total Images in Database: ${images.length}`);
    console.log(`   âœ… Existing Files: ${existingCount}`);
    console.log(`   âŒ Missing Files: ${missingCount}`);

    if (missingCount > 0) {
      console.log(`\nâš ï¸ Warning: ${missingCount} image(s) are in the database but files are missing!`);
    } else {
      console.log("\nðŸŽ‰ All images in database have corresponding files!");
    }

  } catch (error: any) {
    console.error("\nâŒ Error showing images:", error.message);
    throw error;
  }
}

// Run the script
showAllImages()
  .then(() => {
    console.log("\nâœ… Script completed successfully");
    process.exit(0);
  })
  .catch((error) => {
    console.error("\nâŒ Script failed:", error);
    process.exit(1);
  });

server/middleware/performance.ts.content:
import type { Request, Response, NextFunction } from "express";
import type { User } from "@shared/schema";
import { createHash } from 'node:crypto';

// Performance metrics storage (in-memory for development)
interface PerformanceMetric {
  endpoint: string;
  method: string;
  responseTime: number;
  timestamp: Date;
  statusCode: number;
  userId?: string;
  userAgent?: string;
  ip: string;
}

class PerformanceMonitor {
  private metrics: PerformanceMetric[] = [];
  private readonly MAX_METRICS = 1000; // Keep last 1000 metrics

  addMetric(metric: PerformanceMetric) {
    this.metrics.push(metric);

    // Keep only the latest metrics to prevent memory issues
    if (this.metrics.length > this.MAX_METRICS) {
      this.metrics = this.metrics.slice(-this.MAX_METRICS);
    }
  }

  getMetrics(hours = 1): PerformanceMetric[] {
    const cutoff = new Date(Date.now() - (hours * 60 * 60 * 1000));
    return this.metrics.filter(metric => metric.timestamp >= cutoff);
  }

  getAverageResponseTime(endpoint?: string, hours = 1): number {
    const metrics = this.getMetrics(hours);
    const filtered = endpoint 
      ? metrics.filter(m => m.endpoint === endpoint)
      : metrics;

    if (filtered.length === 0) return 0;

    const total = filtered.reduce((sum, metric) => sum + metric.responseTime, 0);
    return Math.round(total / filtered.length);
  }

  getSlowRequests(thresholdMs = 1000, hours = 1): PerformanceMetric[] {
    return this.getMetrics(hours)
      .filter(metric => metric.responseTime > thresholdMs)
      .sort((a, b) => b.responseTime - a.responseTime);
  }

  getEndpointStats(hours = 1): Record<string, {
    count: number;
    avgResponseTime: number;
    slowRequests: number;
  }> {
    const metrics = this.getMetrics(hours);
    const stats: Record<string, {
      count: number;
      totalTime: number;
      slowRequests: number;
    }> = {};

    metrics.forEach(metric => {
      const key = `${metric.method} ${metric.endpoint}`;
      if (!stats[key]) {
        stats[key] = { count: 0, totalTime: 0, slowRequests: 0 };
      }

      stats[key].count++;
      stats[key].totalTime += metric.responseTime;
      if (metric.responseTime > 1000) {
        stats[key].slowRequests++;
      }
    });

    // Convert to final format
    return Object.entries(stats).reduce((result, [key, data]) => {
      result[key] = {
        count: data.count,
        avgResponseTime: Math.round(data.totalTime / data.count),
        slowRequests: data.slowRequests
      };
      return result;
    }, {} as Record<string, { count: number; avgResponseTime: number; slowRequests: number; }>);
  }
}

export const performanceMonitor = new PerformanceMonitor();

// Enhanced performance logging middleware
export function performanceLogger(req: Request, res: Response, next: NextFunction) {
  const start = Date.now();
  const path = req.path;

  // Skip non-API routes for performance tracking
  if (!path.startsWith("/api")) {
    return next();
  }

  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    const user = (req as any).user as User | undefined;

    // Store detailed performance metrics
    performanceMonitor.addMetric({
      endpoint: path,
      method: req.method,
      responseTime: duration,
      timestamp: new Date(),
      statusCode: res.statusCode,
      userId: user?.id,
      userAgent: req.get('User-Agent'),
      ip: req.ip || req.connection.remoteAddress || 'unknown'
    });

    // Create log line
    let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
    if (capturedJsonResponse) {
      logLine += ` : ${JSON.stringify(capturedJsonResponse)}`;
    }

    if (logLine.length > 80) {
      logLine = logLine.slice(0, 79) + "â€¦";
    }

    // Color code based on response time
    if (duration > 2000) {
      console.log(`ðŸ”´ SLOW: ${logLine}`);
    } else if (duration > 1000) {
      console.log(`ðŸŸ¡ WARN: ${logLine}`);
    } else {
      console.log(logLine);
    }

    // Log warning for slow requests
    if (duration > 2000) {
      console.warn(`âš ï¸ Slow API request detected: ${req.method} ${path} took ${duration}ms`);
    }
  });

  next();
}

// Cache control middleware for different types of content
export function cacheControl(options: {
  maxAge?: number;
  sMaxAge?: number;
  noCache?: boolean;
  noStore?: boolean;
  mustRevalidate?: boolean;
  immutable?: boolean;
}) {
  return (req: Request, res: Response, next: NextFunction) => {
    const cacheDirectives: string[] = [];

    if (options.noCache) {
      cacheDirectives.push('no-cache');
    }

    if (options.noStore) {
      cacheDirectives.push('no-store');
    }

    if (options.mustRevalidate) {
      cacheDirectives.push('must-revalidate');
    }

    if (options.maxAge !== undefined) {
      cacheDirectives.push(`max-age=${options.maxAge}`);
    }

    if (options.sMaxAge !== undefined) {
      cacheDirectives.push(`s-maxage=${options.sMaxAge}`);
    }

    if (options.immutable) {
      cacheDirectives.push('immutable');
    }

    if (cacheDirectives.length > 0) {
      res.set('Cache-Control', cacheDirectives.join(', '));
    }

    next();
  };
}

// ETag generation for cacheable content with stable hashing
export function generateETag(data: any): string {
  try {
    // Stable JSON serialization with sorted keys
    const stableStringify = (obj: any): string => {
      if (obj === null || typeof obj !== 'object') {
        if (obj instanceof Date) {
          return obj.toISOString();
        }
        if (typeof obj === 'bigint') {
          return obj.toString();
        }
        return String(obj);
      }

      if (Array.isArray(obj)) {
        return '[' + obj.map(stableStringify).join(',') + ']';
      }

      // Sort object keys for consistency
      const sortedKeys = Object.keys(obj).sort();
      const pairs = sortedKeys.map(key => 
        '"' + key + '":' + stableStringify(obj[key])
      );
      return '{' + pairs.join(',') + '}';
    };

    const stableString = stableStringify(data);
    return createHash('md5')
      .update(stableString)
      .digest('hex');
  } catch (error) {
    console.warn('ETag generation failed:', error);
    // Fallback to a deterministic ETag based on error and data type
    const fallbackData = `etag-error-fallback-${typeof data}-${String(data).slice(0, 100)}`;
    return createHash('md5')
      .update(fallbackData)
      .digest('hex');
  }
}

// Conditional request handling
export function handleConditionalRequest(req: Request, res: Response, data: any, lastModified?: Date): boolean {
  const etag = generateETag(data);

  // Set ETag header
  res.set('ETag', `"${etag}"`);

  // Set Last-Modified if provided
  if (lastModified) {
    res.set('Last-Modified', lastModified.toUTCString());
  }

  // Check If-None-Match (ETag)
  const ifNoneMatch = req.get('If-None-Match');
  if (ifNoneMatch === `"${etag}"`) {
    res.status(304).end();
    return true;
  }

  // Check If-Modified-Since
  if (lastModified) {
    const ifModifiedSince = req.get('If-Modified-Since');
    if (ifModifiedSince) {
      const ifModifiedSinceDate = new Date(ifModifiedSince);
      if (lastModified <= ifModifiedSinceDate) {
        res.status(304).end();
        return true;
      }
    }
  }

  return false;
}

// Request size monitoring
export function requestSizeMonitor(maxSizeMB = 10) {
  return (req: Request, res: Response, next: NextFunction) => {
    const contentLength = req.get('Content-Length');
    if (contentLength) {
      const sizeMB = parseInt(contentLength) / (1024 * 1024);
      if (sizeMB > maxSizeMB) {
        console.warn(`âš ï¸ Large request detected: ${req.method} ${req.path} - ${sizeMB.toFixed(2)}MB`);
      }
    }
    next();
  };
}

// Database query performance tracking
export function trackQueryPerformance<T>(
  queryName: string,
  queryFn: () => Promise<T>
): Promise<T> {
  return new Promise(async (resolve, reject) => {
    const start = Date.now();

    try {
      const result = await queryFn();
      const duration = Date.now() - start;

      if (duration > 500) {
        console.warn(`ðŸŒ Slow database query: ${queryName} took ${duration}ms`);
      } else if (duration > 100) {
        console.log(`ðŸ“Š Query: ${queryName} completed in ${duration}ms`);
      }

      resolve(result);
    } catch (error) {
      const duration = Date.now() - start;
      console.error(`âŒ Query failed: ${queryName} after ${duration}ms:`, error);
      reject(error);
    }
  });
}

