              {/* Properties Table */}
              <div className="border border-orange-200 rounded-lg overflow-hidden">
                {propertiesLoading ? (
                  <div className="p-8 text-center">
                    <div className="animate-spin h-8 w-8 border-4 border-orange-600 border-t-transparent rounded-full mx-auto"></div>
                    <p className="mt-4 text-gray-600">Loading properties...</p>
                  </div>
                ) : filteredProperties.length === 0 ? (
                  <div className="p-8 text-center text-gray-500">
                    <Building2 className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                    <p>No properties found</p>
                  </div>
                ) : (
                  <div className="overflow-x-auto">
                    <table className="w-full border-collapse">
                      <thead>
                        <tr className="border-b border-orange-200 dark:border-gray-600 bg-orange-50 dark:bg-gray-700">
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Image</th>
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Title</th>
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Type</th>
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Location</th>
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Price</th>
                          <th className="text-left py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Status</th>
                          <th className="text-right py-3 px-4 font-semibold text-gray-700 dark:text-gray-300">Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {filteredProperties.map((property) => (
                          <tr key={property.id} className="border-b border-gray-100 dark:border-gray-700 hover:bg-orange-25 dark:hover:bg-gray-750" data-testid={`row-property-${property.id}`}>
                            <td className="py-3 px-4">
                              {property.images && property.images.length > 0 ? (
                                <img
                                  src={normalizePropertyImageUrl(property.images[0])}
                                  alt={property.title}
                                  className="w-16 h-16 object-cover rounded"
                                />
                              ) : (
                                <div className="w-16 h-16 bg-gray-200 rounded flex items-center justify-center">
                                  <ImageIcon className="h-6 w-6 text-gray-400" />
                                </div>
                              )}
                            </td>
                            <td className="py-3 px-4">
                              <div className="max-w-xs">
                                <p className="font-semibold text-gray-800 dark:text-gray-200 truncate">{property.title}</p>
                                <p className="text-xs text-gray-500 dark:text-gray-400 truncate">{property.bedrooms}bd â€¢ {property.bathrooms}ba â€¢ {property.area}mÂ²</p>
                              </div>
                            </td>
                            <td className="py-3 px-4">
                              <Badge variant="outline" className="capitalize">{property.type}</Badge>
                            </td>
                            <td className="py-3 px-4">
                              <div className="max-w-xs">
                                <p className="text-sm text-gray-700 dark:text-gray-300 truncate">{property.city}</p>
                                <p className="text-xs text-gray-500 dark:text-gray-400 truncate">{property.country}</p>
                              </div>
                            </td>
                            <td className="py-3 px-4">
                              <div className="flex flex-col">
                                <span className="font-bold text-green-600">${parseFloat(property.price).toLocaleString()}</span>
                                <Badge className={`text-xs mt-1 ${
                                  property.listingType === 'sale' 
                                    ? 'bg-red-100 text-red-800 hover:bg-red-200' 
                                    : 'bg-green-100 text-green-800 hover:bg-green-200'
                                }`}>
                                  {property.listingType === 'sale' ? 'Sale' : 'Rent'}
                                </Badge>
                              </div>
                            </td>
                            <td className="py-3 px-4">
                              <Badge variant={property.status === 'active' ? 'default' : 'secondary'} className={property.status === 'active' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'}>
                                {property.status}
                              </Badge>
                            </td>
                            <td className="py-3 px-4 text-right">
                              <div className="flex justify-end gap-2">
                                <Link href={`/en/properties/${property.slug || property.id}`}>
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    className="text-blue-600 border-blue-200 hover:bg-blue-50"
                                    data-testid={`button-view-${property.id}`}
                                  >
                                    <Eye className="h-4 w-4" />
                                  </Button>
                                </Link>
                                <Button
                                  variant="outline"
                                  size="sm"
                                  onClick={() => handleDeleteProperty(property.id, property.title)}
                                  disabled={deletePropertyMutation.isPending}
                                  className="text-red-600 border-red-200 hover:bg-red-50"
                                  data-testid={`button-delete-property-${property.id}`}
                                >
                                  <Trash2 className="h-4 w-4" />
                                </Button>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Edit Currency Rate Modal */}
        <Dialog open={isEditCurrencyRateOpen} onOpenChange={setIsEditCurrencyRateOpen}>
          <DialogContent className="sm:max-w-md">
            <DialogHeader>
              <DialogTitle>Edit Exchange Rate</DialogTitle>
              <DialogDescription>
                Update the exchange rate from 1 USD to {editingCurrencyRate?.toCurrency}
              </DialogDescription>
            </DialogHeader>
            <Form {...editCurrencyRateForm}>
              <form onSubmit={editCurrencyRateForm.handleSubmit(onEditCurrencyRate)} className="space-y-6">
                <FormField
                  control={editCurrencyRateForm.control}
                  name="rate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Exchange Rate (1 USD = {editingCurrencyRate?.toCurrency})</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          type="number"
                          step="0.000001"
                          placeholder="e.g., 1173.5"
                          data-testid="input-edit-exchange-rate"
                          onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={editCurrencyRateForm.control}
                  name="isActive"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4">
                      <div className="space-y-0.5">
                        <FormLabel className="text-base">Active Rate</FormLabel>
                        <FormDescription>
                          Make this rate active for user transactions
                        </FormDescription>
                      </div>
                      <FormControl>
                        <Checkbox
                          checked={field.value ?? false}
                          onCheckedChange={field.onChange}
                          data-testid="checkbox-edit-is-active"
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button type="button" variant="outline" onClick={() => setIsEditCurrencyRateOpen(false)} data-testid="button-cancel-edit">
                    Cancel
                  </Button>
                  <Button type="submit" disabled={updateCurrencyRateMutation.isPending} data-testid="button-submit-edit">
                    {updateCurrencyRateMutation.isPending ? 'Updating...' : 'Update Rate'}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>

        {/* Customer Details Modal */}
        <Dialog open={isCustomerDetailsOpen} onOpenChange={setIsCustomerDetailsOpen}>
          <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center space-x-3">
                <Avatar className="h-12 w-12 ring-2 ring-orange-200">
                  <AvatarImage src={selectedCustomer?.avatar || ''} />
                  <AvatarFallback className="bg-orange-100 text-orange-700 font-medium text-lg">
                    {selectedCustomer?.firstName?.[0]}{selectedCustomer?.lastName?.[0] || selectedCustomer?.username[0]}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h2 className="text-2xl font-bold">
                    {selectedCustomer?.firstName && selectedCustomer?.lastName 
                      ? `${selectedCustomer.firstName} ${selectedCustomer.lastName}` 
                      : selectedCustomer?.username}
                  </h2>
                  <p className="text-gray-600">{selectedCustomer?.email}</p>
                </div>
              </DialogTitle>
              <DialogDescription>
                Complete customer profile and property portfolio
              </DialogDescription>
            </DialogHeader>

            {selectedCustomer && (
              <Tabs defaultValue="overview" className="w-full">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="overview">Overview</TabsTrigger>
                  <TabsTrigger value="properties">Properties</TabsTrigger>
                  <TabsTrigger value="analytics">Analytics</TabsTrigger>
                </TabsList>

                <TabsContent value="overview" className="space-y-6 mt-6">
                  {/* Customer Information Cards */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {/* Basic Info */}
                    <Card>
                      <CardHeader className="pb-3">
                        <CardTitle className="text-sm font-medium flex items-center">
                          <Users className="h-4 w-4 mr-2" />
                          Customer Details
                        </CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-2">
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Username:</span>
                          <span className="font-medium">{selectedCustomer.username}</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Role:</span>
                          <Badge variant={selectedCustomer.role === 'admin' ? 'destructive' : 'secondary'}>
                            {selectedCustomer.role === 'agent' ? 'Agent' : selectedCustomer.role === 'admin' ? 'Admin' : 'Customer'}
                          </Badge>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Status:</span>
                          <Badge 
                            variant={selectedCustomer.isVerified ? 'default' : 'secondary'}
                            className={`${
                              selectedCustomer.isVerified 
                                ? 'bg-orange-100 text-orange-800 hover:bg-orange-200 border-orange-300' 
                                : 'bg-yellow-100 text-yellow-800 hover:bg-yellow-200'
                            }`}
                          >
                            {selectedCustomer.isVerified ? 'Verified' : 'Pending'}
                          </Badge>
                        </div>
                        {selectedCustomer.phone && (
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-600">Phone:</span>
                            <span className="font-medium">{selectedCustomer.phone}</span>
                          </div>
                        )}
                      </CardContent>
                    </Card>

                    {/* Statistics */}
                    <Card>
                      <CardHeader className="pb-3">
                        <CardTitle className="text-sm font-medium flex items-center">
                          <BarChart3 className="h-4 w-4 mr-2" />
                          Portfolio Stats
                        </CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-2">
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Total Properties:</span>
                          <span className="font-bold text-lg text-orange-600">{customerProperties.length}</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">For Sale:</span>
                          <span className="font-medium">{customerProperties.filter(p => p.listingType === 'sale').length}</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">For Rent:</span>
                          <span className="font-medium">{customerProperties.filter(p => p.listingType === 'rent').length}</span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Total Value:</span>
                          <span className="font-medium text-green-600">
                            ${customerProperties.reduce((sum, p) => sum + (p.price || 0), 0).toLocaleString()}
                          </span>
                        </div>
                      </CardContent>
                    </Card>

                    {/* Account Info */}
                    <Card>
                      <CardHeader className="pb-3">
                        <CardTitle className="text-sm font-medium flex items-center">
                          <Calendar className="h-4 w-4 mr-2" />
                          Account Info
                        </CardTitle>
                      </CardHeader>
                      <CardContent className="space-y-2">
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Joined:</span>
                          <span className="font-medium">
                            {selectedCustomer.createdAt ? new Date(selectedCustomer.createdAt).toLocaleDateString() : 'N/A'}
                          </span>
                        </div>
                        <div className="flex justify-between text-sm">
                          <span className="text-gray-600">Expiration:</span>
                          <span className="font-medium">
                            {(() => {
                              const daysUntilExpiration = calculateDaysUntilExpiration(selectedCustomer.expiresAt);
                              const { status, color } = getExpirationStatus(daysUntilExpiration);
                              return <span className={color}>{status}</span>;
                            })()}
                          </span>
                        </div>
                      </CardContent>
                    </Card>
                  </div>
                </TabsContent>

                <TabsContent value="properties" className="space-y-6 mt-6">
                  {/* Properties Section */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="flex items-center justify-between">
                        <span className="flex items-center">
                          <Building2 className="h-5 w-5 mr-2" />
                          Properties Portfolio ({customerProperties.length})
                        </span>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      {customerPropertiesLoading ? (
                        <div className="flex items-center justify-center py-8">
                          <div className="animate-spin h-8 w-8 border-4 border-blue-600 border-t-transparent rounded-full"></div>
                          <span className="ml-3">Loading properties...</span>
                        </div>
                      ) : customerProperties.length === 0 ? (
                        <div className="text-center py-8 text-gray-500">
                          <Building2 className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                          <p>No properties found for this customer</p>
                        </div>
                      ) : (
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                          {customerProperties.map((property) => (
                            <Card key={property.id} className="overflow-hidden hover:shadow-lg transition-shadow">
                              <div className="relative">
                                {property.images && property.images.length > 0 ? (
                                  <img
                                    src={normalizePropertyImageUrl(property.images[0].imageUrl || property.images[0])}
                                    alt={property.title}
                                    className="w-full h-32 object-cover"
                                  />
                                ) : (
                                  <div className="w-full h-32 bg-gray-200 flex items-center justify-center">
                                    <ImageIcon className="h-8 w-8 text-gray-400" />
                                  </div>
                                )}
                                <Badge 
                                  className={`absolute top-2 right-2 ${
                                    property.listingType === 'sale' 
                                      ? 'bg-red-500 hover:bg-red-600' 
                                      : 'bg-green-500 hover:bg-green-600'
                                  }`}
                                >
                                  {property.listingType === 'sale' ? 'For Sale' : 'For Rent'}
                                </Badge>
                              </div>
                              <CardContent className="p-4">
                                <h3 className="font-semibold text-sm mb-2 line-clamp-1">{property.title}</h3>
                                <div className="space-y-1 text-xs text-gray-600">
                                  <div className="flex items-center">
                                    <MapPin className="h-3 w-3 mr-1" />
                                    <span className="line-clamp-1">{property.location}</span>
                                  </div>
                                  <div className="flex items-center">
                                    <Home className="h-3 w-3 mr-1" />
                                    <span>{property.type}</span>
                                  </div>
                                  <div className="flex items-center justify-between mt-2">
                                    <div className="flex items-center">
                                      <DollarSign className="h-3 w-3 mr-1" />
                                      <span className="font-bold text-green-600">
                                        ${property.price?.toLocaleString()}
                                      </span>
                                    </div>
                                    <div className="text-xs text-gray-500">
                                      {property.bedrooms}bd â€¢ {property.bathrooms}ba
                                    </div>
                                  </div>
                                </div>
                              </CardContent>
                            </Card>
                          ))}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="analytics" className="mt-6">
                  <CustomerAnalytics 
                    customerId={selectedCustomer.id} 
                    customerName={selectedCustomer.firstName && selectedCustomer.lastName 
                      ? `${selectedCustomer.firstName} ${selectedCustomer.lastName}` 
                      : selectedCustomer.username}
                  />
                </TabsContent>
              </Tabs>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

